mapper.select:
SELECT
    f.*
FROM catalog_function f
JOIN catalog_database d ON f.database_id = d.id
WHERE d.type = #{type}
AND d.catalog = #{catalog}
AND d.`name` = #{database}
ORDER BY f.`name`, f.create_time

SELECT COUNT(*)
FROM catalog_function f
         JOIN catalog_database d ON f.database_id = d.id
WHERE d.type = #{type}
  AND d.catalog = #{catalog}
  AND d.`name` = #{database}

SELECT
    f.*
FROM catalog_function f
JOIN catalog_database d ON f.database_id = d.id
WHERE d.type = #{type}
AND d.catalog = #{catalog}
AND d.`name` = #{database}
AND f.`name` = #{name}

mapper.update:
UPDATE catalog_function
SET delete_time = NOW()
WHERE id = (SELECT ct.id
            FROM (SELECT f.id
                  FROM catalog_function f
                           JOIN catalog_database d ON f.database_id = d.id
                  WHERE d.type = #{type}
                    AND d.catalog = #{catalog}
                    AND d.`name` = #{database}
                    AND f.`name` = #{name}) AS ct)
